></div>\r\n            <div style=\"color: #64748b;\">Memuat detail perbandingan...</div>\r\n        </div>\r\n    `;\r\n    \r\n    modal.classList.add(\'active\');\r\n    \r\n    // Simulate loading and fetch data\r\n    setTimeout(() => {\r\n        // Calculate similarity level\r\n        const similarity = parseFloat(data.similarity);\r\n        let similarityLevel = \'low\';\r\n        let similarityColor = \'#10b981\';\r\n        if (similarity >= 50) {\r\n            similarityLevel = \'high\';\r\n            similarityColor = \'#ef4444\';\r\n        } else if (similarity >= 25) {\r\n            similarityLevel = \'medium\';\r\n            similarityColor = \'#f59e0b\';\r\n        }\r\n        \r\n        // Generate modal content\r\n        modalBody.innerHTML = `\r\n            <!-- Document Information -->\r\n            <div class=\"document-info\">\r\n                <h3 style=\"margin: 0 0 15px 0; color: #1e293b; font-size: 1.2rem;\">\r\n                    📄 ${data.judul}\r\n                </h3>\r\n                <div class=\"document-info-grid\">\r\n                    <div class=\"info-item\">\r\n                        <span class=\"info-label\">Penulis</span>\r\n                        <span class=\"info-value\">${data.penulis || \'-\'}</span>\r\n                    </div>\r\n                    <div class=\"info-item\">\r\n                        <span class=\"info-label\">Tahun</span>\r\n                        <span class=\"info-value\">${data.tahun || \'-\'}</span>\r\n                    </div>\r\n                    <div class=\"info-item\">\r\n                        <span class=\"info-label\">Tingkat Kemiripan</span>\r\n                        <span class=\"similarity-badge ${similarityLevel}\">\r\n                            <span>📊</span>\r\n                            <span>${similarity.toFixed(2)}%</span>\r\n                        </span>\r\n                    </div>\r\n                    <div class=\"info-item\">\r\n                        <span class=\"info-label\">Fingerprint Sama</span>\r\n                        <span class=\"info-value\">${data.common_fingerprints} fingerprints</span>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n\r\n            <!-- Statistics -->\r\n            <div class=\"stats-section\">\r\n                <h4 style=\"margin: 0 0 10px 0; color: #0369a1;\">📈 Statistik Perbandingan</h4>\r\n                <div class=\"stats-grid\">\r\n                    <div class=\"stat-card\">\r\n                        <div class=\"stat-value\" style=\"color: ${similarityColor};\">\r\n                            ${similarity.toFixed(1)}%\r\n                        </div>\r\n                        <div class=\"stat-label\">Kemiripan Total</div>\r\n                    </div>\r\n                    <div class=\"stat-card\">\r\n                        <div class=\"stat-value\">${data.common_fingerprints}</div>\r\n                        <div class=\"stat-label\">Fingerprint Sama</div>\r\n                    </div>\r\n                    <div class=\"stat-card\">\r\n                        <div class=\"stat-value\">${data.total_fingerprints_doc || \'-\'}</div>\r\n                        <div class=\"stat-label\">Total Fingerprint Dokumen</div>\r\n                    </div>\r\n                    <div class=\"stat-card\">\r\n                        <div class=\"stat-value\">${data.total_fingerprints_input || \'-\'}</div>\r\n                        <div class=\"stat-label\">Total Fingerprint Input</div>\r\n                    </div>\r\n                </div>\r\n            </div>\r\n\r\n            <!-- Text Comparison -->\r\n            <h4 style=\"margin: 25px 0 10px 0; color: #1e293b;\">📝 Perbandingan Teks</h4>\r\n            <div class=\"comparison-container\">\r\n                <div class=\"text-panel\">\r\n                    <div class=\"text-panel-header\">\r\n                        <span>📥</span>\r\n                        <span>Teks Input (Abstrak yang Diuji)</span>\r\n                    </div>\r\n                    <div class=\"text-panel-body\">\r\n                        ${highlightSimilarText(inputText || \'Teks input tidak tersedia\', data.matched_segments || [])}\r\n                    </div>\r\n                </div>\r\n                <div class=\"text-panel\">\r\n                    <div class=\"text-panel-header\">\r\n                        <span>📚</span>\r\n                        <span>Teks Dokumen Database</span>\r\n                    </div>\r\n                    <div class=\"text-panel-body\">\r\n                        ${highlightSimilarText(data.abstrak || \'Abstrak tidak tersedia\', data.matched_segments || [])}\r\n                    </div>\r\n                </div>\r\n            </div>\r\n\r\n            <!-- Matched Segments -->\r\n            ${generateMatchedSegments(data.matched_segments || [])}\r\n\r\n            <!-- Action Buttons -->\r\n            <div style=\"display: flex; gap: 10px; margin-top: 25px; padding-top: 20px; border-top: 1px solid #e2e8f0;\">\r\n                <button onclick=\"viewFullDocument(${data.id})\" style=\"\r\n                    background: linear-gradient(135deg, #3b82f6, #2563eb);\r\n                    color: white;\r\n                    border: none;\r\n                    padding: 10px 20px;\r\n                    border-radius: 6px;\r\n                    cursor: pointer;\r\n                    font-weight: 500;\r\n                \">\r\n                    📄 Lihat Dokumen Lengkap\r\n                </button>\r\n                <button onclick=\"downloadComparison(${data.id})\" style=\"\r\n                    background: linear-gradient(135deg, #10b981, #059669);\r\n                    color: white;\r\n                    border: none;\r\n                    padding: 10px 20px;\r\n                    border-radius: 6px;\r\n                    cursor: pointer;\r\n                    font-weight: 500;\r\n                \">\r\n                    📥 Download Laporan\r\n                </button>\r\n                <button onclick=\"closeDetailModal()\" style=\"\r\n                    background: #f1f5f9;\r\n                    color: #475569;\r\n                    border: 1px solid #cbd5e1;\r\n                    padding: 10px 20px;\r\n                    border-radius: 6px;\r\n                    cursor: pointer;\r\n                    font-weight: 500;\r\n                    margin-left: auto;\r\n                \">\r\n                    Tutup\r\n                </button>\r\n            </div>\r\n        `;\r\n    }, 500);\r\n}\r\n\r\n// Function to close detail modal\r\nfunction closeDetailModal() {\r\n    const modal = document.getElementById(\'detailModal\');\r\n    modal.classList.remove(\'active\');\r\n}\r\n\r\n// Function to highlight similar text\r\nfunction highlightSimilarText(text, matchedSegments) {\r\n    if (!text || matchedSegments.length === 0) {\r\n        return text;\r\n    }\r\n    \r\n    // Create a simple highlight for demonstration\r\n    // In real implementation, you would use the matched segments\r\n    let highlightedText = text;\r\n    \r\n    // Simulate highlighting some portions\r\n    const words = text.split(\' \');\r\n    const highlightIndices = generateRandomIndices(words.length, Math.min(10, words.length / 3));\r\n    \r\n    highlightIndices.forEach(index => {\r\n        if (words[index] && words[index].length > 3) {\r\n            words[index] = `<span class=\"highlight-match\">${words[index]}</span>`;\r\n        }\r\n    });\r\n    \r\n    return words.join(\' \');\r\n}\r\n\r\n// Helper function to generate random indices for demonstration\r\nfunction generateRandomIndices(max, count) {\r\n    const indices = [];\r\n    while (indices.length < count) {\r\n        const randomIndex = Math.floor(Math.random() * max);\r\n        if (!indices.includes(randomIndex)) {\r\n            indices.push(randomIndex);\r\n        }\r\n    }\r\n    return indices;\r\n}\r\n